--TABLAS

--1. products 

CREATE TABLE products (
product_id SERIAL PRIMARY KEY,
name VARCHAR(100),
category VARCHAR(50),
brand VARCHAR(50)
);

--2.stores 

CREATE TABLE stores (
store_id SERIAL PRIMARY KEY,
name VARCHAR(100),
city VARCHAR(50),
latitude FLOAT,
longitude FLOAT
);

--3. weather 

CREATE TABLE weather_observations (
    id SERIAL PRIMARY KEY,
    city VARCHAR(50),
    date DATE,
    temperature FLOAT,
    precipitation FLOAT,
    humidity FLOAT,
    weather_description VARCHAR(100),
    latitude FLOAT,
    longitude FLOAT,
    UNIQUE (city, date)
);

--4. sales 

CREATE TABLE sales (
id SERIAL PRIMARY KEY,
date DATE,
product_id INT REFERENCES products(product_id),
store_id INT REFERENCES stores(store_id),
sales INT,
price FLOAT
);

--5. sales_predictions (predicciones)

CREATE TABLE sales_predictions (
id SERIAL PRIMARY KEY,
date DATE,
product_id INT REFERENCES products(product_id),
store_id INT REFERENCES stores(store_id),
price FLOAT,
temperature FLOAT,
category VARCHAR(50),
sales_prediction FLOAT
);

--Tablas de respaldo

CREATE TABLE backup_sales (
id SERIAL PRIMARY KEY,
original_sale_id INT,
date DATE,
product_id INT,
store_id INT,
sales INT,
price FLOAT,
backup_timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

--Trigger

CREATE OR REPLACE FUNCTION backup_sales_function()
RETURNS TRIGGER AS $$
BEGIN
    original_sale_id, date, product_id, store_id, sales, price
  ) VALUES (
    NEW.id, NEW.date, NEW.product_id, NEW.store_id, NEW.sales, NEW.price
  );
  RETURN NEW;
END;
$$ LANGUAGE plpgsql;

CREATE TRIGGER trg_backup_sales
AFTER INSERT ON sales
FOR EACH ROW
EXECUTE FUNCTION backup_sales_function();

