SELECT
  s.id,
  s.date,
  p.name AS product_name,
  p.category,
  st.name AS store_name,
  st.city,
  s.sales,
  s.price
FROM sales s
JOIN products p ON s.product_id = p.product_id
JOIN stores st ON s.store_id = st.store_id
ORDER BY s.date, store_name, product_name;


